command:example.dev:code:
Merge-NAVApplicationObject -OriginalPath .\ORIGINAL\*.txt -ModifiedPath .\MODIFIED\*.txt -TargetPath .\TARGET\*.txt -ResultPath
.\RESULT -Force -PassThru | Where-Object MergeResult -eq 'Merged' | foreach { Set-NAVApplicationObjectProperty -TargetPath
$_.ResultPath -DateTimeProperty (Get-Date -Year 2017 -Month 1 -Day 1 -Hour 0 -Minute 0 -Format g) }
